$(document).ready(function() {
  var myTimer;

	var timeMultiplier;

	var progressBar;

	var pomo = true;

	var pomodoroSpinner = $("#pomodoro-spinner").spinner();
	pomodoroSpinner.spinner({ min: 1, max: 90 }).val(25).width(30);
	var breakSpinner = $("#break-spinner").spinner();
	breakSpinner.spinner({ min: 1, max: 30 }).val(5).width(30);

  var display = document.querySelector('#time');
	var displayBox = $("#time");
	displayBox.html('00:00');


	var messageArea = $("#message-area");

	var startButton = $('#btn-start');

	startButton.click(function() {
		var pomoDuration = pomodoroSpinner.spinner("value") * $("input[name='radio-1']:checked").val();

		if (startButton.html() === 'start') {
			startButton.html('stop');
			startButton.css("background-color","red");
			messageArea.html('Get some work done!');
			pomodoroSpinner.spinner("disable");
			breakSpinner.spinner("disable");
			$("input[type=radio]").attr('disabled', true);
			startProgressBar ('green', pomoDuration);
  		startTimer(pomoDuration, display);
		} else {
			startButton.html('start');
			startButton.css("background-color","green");
			displayBox.html('00:00');
			messageArea.html('Press start when you\'re ready to get some work done.');
			pomodoroSpinner.spinner("enable");
			breakSpinner.spinner("enable");
			$("input[type=radio]").attr('disabled', false);
			progressBar.destroy();
			progressBar = undefined;

  		clearInterval(myTimer);
		}
	}); //start button click


	function startTimer(duration, display) {
    var start = Date.now(),
        diff,
        minutes,
        seconds;
		pomo = true;

    function timer() {
				var pomoDuration = pomodoroSpinner.spinner("value") * $("input[name='radio-1']:checked").val();
				var breakDuration = breakSpinner.spinner("value") * $("input[name='radio-1']:checked").val();
        diff = (duration /* * 60 */) - (((Date.now() - start) / 1000) | 0);

        minutes = (diff / 60) | 0;
        seconds = (diff % 60) | 0;

        minutes = minutes < 10 ? "0" + minutes : minutes;
        seconds = seconds < 10 ? "0" + seconds : seconds;

        display.textContent = minutes + ":" + seconds;

        if (diff <= 0) {
						if (pomo) {
							duration = breakDuration;
							messageArea.html('Break time!');
							pomo = false;
							startProgressBar('red', breakDuration);
							beep();
						} else {
							duration = pomoDuration;
							messageArea.html('Get some work done!');
							pomo = true;
							startProgressBar('green', pomoDuration);
							beep();
						}
						diff = (duration /* * 60 */) - (((Date.now() - start) / 1000) | 0);
            start = Date.now();
        }
    	}; // timer
		timer();
    myTimer = setInterval(timer, 100);
	}; //startTimer

	function startProgressBar (color, time) {
		if (progressBar !== undefined) {
			progressBar.destroy();
		}
		progressBar = new ProgressBar.Circle(progress, {
			strokeWidth: 6,
			easing: 'linear',
			duration: time * 1000,
			color: color,
			trailColor: '#eee',
			trailWidth: 1,
			svgStyle: null
		});
		progressBar.animate(1.0);
	}

	function beep() {
    var snd = new Audio("data:audio/wav;base64,");
    snd.play();
	} // beep()

}); // document ready
